import (
	"log"
	"net"
	"os"

	"github.com/maxmind/mmdbwriter"
	"github.com/maxmind/mmdbwriter/inserter"
	"github.com/maxmind/mmdbwriter/mmdbtype"
)

func main() {
	fh, err := os.Create("../test-geoip.mmdb")
	if err != nil {
		log.Fatal(err)
	}
	defer fh.Close()

	writer, err := mmdbwriter.New(
		mmdbwriter.Options{
			DatabaseType: "Test-GeoIP-DB",
			RecordSize:   24,
			IPVersion:    4,
		},
	)
	if err != nil {
		log.Fatal(err)
	}

	usData := mmdbtype.Map{
		"country": mmdbtype.Map{
			"iso_code": mmdbtype.String("US"),
		},
		"continent": mmdbtype.Map{
			"code": mmdbtype.String("NA"),
		},
		"city": mmdbtype.Map{
			"names": mmdbtype.Map{
				"en": mmdbtype.String("New York"),
			},
		},
	}

	ukData := mmdbtype.Map{
		"country": mmdbtype.Map{
			"iso_code": mmdbtype.String("GB"),
		},
		"continent": mmdbtype.Map{
			"code": mmdbtype.String("EU"),
		},
		"city": mmdbtype.Map{
			"names": mmdbtype.Map{
				"en": mmdbtype.String("London"),
			},
		},
	}

	jpData := mmdbtype.Map{
		"country": mmdbtype.Map{
			"iso_code": mmdbtype.String("JP"),
		},
		"continent": mmdbtype.Map{
			"code": mmdbtype.String("AS"),
		},
		"city": mmdbtype.Map{
			"names": mmdbtype.Map{
				"en": mmdbtype.String("Tokyo"),
			},
		},
	}

	caData := mmdbtype.Map{
		"country": mmdbtype.Map{
			"iso_code": mmdbtype.String("CA"),
		},
		"continent": mmdbtype.Map{
			"code": mmdbtype.String("NA"),
		},
		"city": mmdbtype.Map{
			"names": mmdbtype.Map{
				"en": mmdbtype.String("Toronto"),
			},
		},
	}

	deData := mmdbtype.Map{
		"country": mmdbtype.Map{
			"iso_code": mmdbtype.String("DE"),
		},
		"continent": mmdbtype.Map{
			"code": mmdbtype.String("EU"),
		},
		"city": mmdbtype.Map{
			"names": mmdbtype.Map{
				"en": mmdbtype.String("Berlin"),
			},
		},
	}

	insertIP(writer, "192.200.0.1/32", usData)
	insertIP(writer, "192.200.0.2/32", ukData)
	insertIP(writer, "192.200.0.3/32", jpData)
	insertIP(writer, "192.200.0.4/32", caData)
	insertIP(writer, "192.200.0.5/32", deData)

	insertIP(writer, "192.201.0.1/32", usData)
	insertIP(writer, "192.201.0.2/32", ukData)
	insertIP(writer, "192.201.0.3/32", jpData)

	_, err = writer.WriteTo(fh)
	if err != nil {
		log.Fatal(err)
	}
}

func insertIP(writer *mmdbwriter.Tree, cidr string, data mmdbtype.Map) {
	_, network, err := net.ParseCIDR(cidr)
	if err != nil {
		log.Fatalf("Error parsing CIDR %s: %v", cidr, err)
	}
	if err := writer.InsertFunc(network, inserter.TopLevelMergeWith(data)); err != nil {
		log.Fatalf("Error inserting %s: %v", cidr, err)
	}
}
